import React, { useEffect, useState } from 'react'
import ReactMarkdown from 'react-markdown'
import { useParams } from 'react-router'
import axios from 'axios'
import Header from './Header'

import './components.css'
import Timeline from './Timeline'

function Layout(props) {
    const url = "http://localhost:1337/events";
    const [event, setEvent] = useState(null);
    const [id, setId] = useState(null);
    
    useEffect(() => {
        axios.get(url)
        .then(response => {
            JSON.stringify(response);
            setEvent(response.data);
            //setId(response.id);
            console.log(response.id);
        });
    }, []);

    if (event) {
        console.log("event found");
        return(
            <div className="layout">
            <Header page="timeline"/>
            <div className="container">
                <div className="information">
                    {/* Info Box */}
                    <ReactMarkdown className="title">{event[0].name}</ReactMarkdown>
                    <ReactMarkdown className="content">{event[0].content}</ReactMarkdown>
                </div>   
                <Timeline />
            </div>
       </div>
        );
    }

    return (
        <div></div>
    )

    return(
        <div className="layout">
            <Header page="timeline"/>
            <div className="container">
                <div className="information">
                    <div className="infoBox">Info</div>
                    
                </div>   
                <Timeline />
            </div>
       </div>
    );
}

export default Layout
